package com.google.appengine.setup.test;

import com.google.appengine.setup.test.util.TestUtil;
import lombok.SneakyThrows;
import org.apache.http.HttpResponse;
import org.apache.http.client.HttpClient;
import org.apache.http.client.methods.HttpGet;
import org.apache.http.util.EntityUtils;
import org.junit.jupiter.api.AfterAll;
import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.BeforeAll;
import org.junit.jupiter.api.Test;
import org.junit.jupiter.api.TestInstance;

@TestInstance(TestInstance.Lifecycle.PER_CLASS)
public abstract class TestAppBase {

    public static final String USER_APPLICATION_URL_PREFIX = "http://localhost:8080/";
    public static final String API_SERVER_URL_PREFIX = "http://localhost:8089/";

    private Process userApp;
    private Process apiServer;

    private HttpClient httpClient = TestUtil.initializeHttpClient(30_000); // Timeout in millis

    protected abstract String appName();

    protected abstract String relativePathForUserApplicationJar();

    @BeforeAll
    public void initialize() {
        userApp = TestUtil.initializeApp(relativePathForUserApplicationJar());
        apiServer = TestUtil.initializeHttpApiServer();
        TestUtil.waitUntilUserAppIsInitialized(httpClient, appName());
        TestUtil.waitUntilApiServerIsInitialized(httpClient);
    }

    @Test
    @SneakyThrows
    void testStatus() {
        String url = USER_APPLICATION_URL_PREFIX + "status";
        HttpResponse response = httpClient.execute(new HttpGet(url));
        String content = EntityUtils.toString(response.getEntity());
        String expectedContent = "{ \"status\": \"ok\"}";
        Assertions.assertEquals(expectedContent, content);
    }

    @Test
    @SneakyThrows
    void testImage() {
        String url = USER_APPLICATION_URL_PREFIX + "image";
        HttpResponse response = httpClient.execute(new HttpGet(url));
        String content = EntityUtils.toString(response.getEntity());
        String expectedContent = "<h1 style='text-align:center;'> Image Processing </h1><h2 style='text-align:center;'> Original Image </h2><p style='text-align:center;'><img src='data:image/png;base64,' alt='image...'></p><h2 style='text-align:center;'> Transformed Image </h2><p style='text-align:center;'><img src='data:image/png;base64,' alt='image...'></p>";
        Assertions.assertEquals(expectedContent, content);
    }

    @Test
    @SneakyThrows
    void testMemcache() {
        String url = USER_APPLICATION_URL_PREFIX + "memcache";
        HttpResponse response = httpClient.execute(new HttpGet(url));
        String content = EntityUtils.toString(response.getEntity());
        String expectedContent = "Setting Memcache Service for key - test_key:(.*), with value - value(.*)<br/>Got Memcache Service for key - test_key:(.*), with value - value(.*)<br/>Memcache Service Stats -  <br/>Hits: (.*)\nMisses: (.*)\nBytes Fetched: (.*)\nBytes Stored: (.*)\nItems: (.*)\nMax Cached Time: (.*)\n";
        Assertions.assertTrue(content.matches(expectedContent));
    }

    @AfterAll
    public void destroy() {
        userApp.destroy();
        apiServer.destroy();
    }
}
